           msg = 'URL has been copied';           break;                    }        alert(msg);      }      function isCheckedAll(selector) {        if(!selector) {          return;        }        var checkbox = document.querySelectorAll(selector);        var result = true              for(var i=0; i<checkbox.length; i++) {          if(checkbox[i].checked === false) {            result = false;          }        }              // console.log('result: ', result);        return result;      }      function checkToggle(selector, uncheck) {        if(!selector) {          return;        }        var checkbox = document.querySelectorAll(selector);              for(var i=0; i<checkbox.length; i++) {          checkbox[i].checked = !uncheck ? true : false;        }      }      function eventAgreementForCheckbox() {        if(!document.querySelector('#agreeAll') || !document.querySelector('#section2 .list_agree')) {          return;        }              // event bind - all체크박스        $('#agreeAll').on('change', function(ev) {          if(ev.currentTarget.checked) {            // 체크박스 모두 체크            checkToggle('#section2 .list_agree .checkbox');          }else {            // 체크박스 모두 체크 해제            checkToggle('#section2 .list_agree .checkbox', true);          }        });      }            window.addEventListener('load', function() {        // 사전예약 입력 폼 > 동의 체크박스 관련 이벤트        eventAgreementForCheckbox();        // event bind - all체크박스를 제외한 나머지 체크박스들        $('#section2 .list_agree .checkbox').on('change', function() {          if(isCheckedAll('#section2 .list_agree .checkbox')) {            document.querySelector('#agreeAll').checked = true;          }else {            document.querySelector('#agreeAll').checked = false;          }        });              // event bind - 자세히보기(read more)        $('#section2 .list_agree .link').on('click', function(ev) {          ev.preventDefault();          var targetSelector = $(ev.currentTarget).attr('href');                // 레이어팝업 examples & API - https://sweetalert2.github.io/#examples          Swal.fire({            showCloseButton: true,            html: document.querySelector(targetSelector).innerHTML,            showConfirmButton: false,            // title: '제목입니다.',            // text: '본문 텍스트 내용입니다.',            // footer: '<a href="https://wemade.com" target="_blank">Footer~~</a>',            // confirmButtonText: '확인',            customClass: {              container: 'layer-container',              popup: 'layer-popup',              title: 'layer-title',              footer: 'layer-footer',            },          });        });        // event bind - 전화번호 국가코드 전환        $('#selectCountryCode').on('change', function(ev) {          if(!document.formRegisterBook.phoneNumber) {            return;          }          // 국가코드 placeholder샘플          var exCountryPhoneNumber = {            tw: '0912341234',            hk: '0812341234',            mo: '0712341234',            sg: '0612341234',            my: '0512341234',          }          // <form name="formRegisterBook">내부에서 name이 phoneNumber인 DOM을 조작          $(document.formRegisterBook.phoneNumber)            .focus()            .val('')            .attr('placeholder', '範例' + exCountryPhoneNumber[this.value]);        });              });      </script>    </section>    <!-- Section #3 -->    <section id="section3" class="section section3">      <div class="section_inner">        <div class="title_box">          <h2>FEATURES</h2>        </div>                <div class="section3_slide">          <div id="swiperFeature" class="swiper-container type1">            <div class="swiper-wrapper">                            <div class="swiper-slide">                <img src="https://file.mir4global.com/m